## online Key path HKLM:\Software\Microsoft\Enterprise\Certificates\Root\Certificates
$CertKey = 'HKLM:\temp\Microsoft\EnterpriseCertificates\Root\Certificates'

reg.exe LOAD HKLM\TEMP "$WinPETemp\Mount\WINDOWS\SYSTEM32\CONFIG\SOFTWARE"
New-Item -Path $CertKey -Name $root1.thumbprint
New-ItemProperty -Path "$CertKey\$($root1.thumbprint)" -Name Blob -PropertyType Binary -Value $root1.Blob
New-Item -Path $CertKey -Name $root2.thumbprint
New-ItemProperty -Path "$CertKey\$($root2.thumbprint)" -Name Blob -PropertyType Binary -Value $root2.Blob
#fix for Access denied uloading registry
0
[gc]::collect()
# end fix
reg.exe UNLOAD HKLM\TEMP
#endregion

#region Replace STartnet to run Powershell and create function
$STartNetContent = {
  echo off
  wpeinit
  powershell.exe -ExecutionPolicy Bypass -NoExit -file x:\WINDOWS\SYSTEM32\function.ps1
}
$FunctionContent = {
  function Get-BuildScript
  {
    [CmdletBinding()]
    Param(
      [Parameter(Mandatory = $true)]
      [string]
      $Script,
    
      [string]
      $Path = 'x:\Start.ps1',
    
      #[Parameter(Mandatory = $true,HelpMessage = 'User with access to TFS')]
      [System.Management.Automation.Credential()]
      [PSCredential]
      $credintial
    )
    if ($credential) {
      $params = @{
        UseBasicParsing = $true
        Credential = $credintial 
        MaximumRedirection = 0 
        ErrorAction = 'SilentlyContinue'
      }
      $response = Invoke-WebRequest @params -Uri $script
      Invoke-WebRequest -UseBasicParsing -Uri $response.Headers.Location -Credential $credintial -OutFile $Path
    }
    else
    {
      $params = @{
        UseBasicParsing = $true
        MaximumRedirection = 0 
        ErrorAction = 'SilentlyContinue'
      }
      $response = Invoke-WebRequest @params -Uri $script
      Invoke-WebRequest -UseBasicParsing -Uri $response.Headers.Location -OutFile $Path
    }
  }
  #Get-BuildScript -Script 'http://bit.ly/SummitDeploy'
  #Write-Output -InputObject 'NewBranch build script saved to x:\start.ps1'
  Write-Output -InputObject "Get-BuildScript -Script 'http://bit.ly/SummitDeploy' to download the build script, then run x:\start.ps1"
}
Set-Content -Path "$WinPETemp\Mount\WINDOWS\SYSTEM32\Startnet.cmd" -Value $STartNetContent
Set-Content -Path "$WinPETemp\Mount\WINDOWS\SYSTEM32\function.ps1" -Value $FunctionContent
#endregion

Dismount-WindowsImage -Path "$WinPETemp\Mount" -Save
#END Mount and update WIM

# create ISO
& "$WinADKTools\Oscdimg\oscdimg.exe" -b"$($WinPETemp)\efisys.bin" -pEF -u1 -udfver102 $WinPETemp\media $WinPETemp\Deploy.iso 2> $null

#region setup USB
{
  $Disk = Get-Disk |
  Where-Object -FilterScript {
    $_.BusType -eq 'USB'
  } |
  Out-GridView -PassThru
  Get-Disk -Number ($Disk.number) |
  Get-Partition |
  Remove-Partition -confirm:$false -ErrorAction SilentlyContinue
  Clear-Disk -Number $Disk.Number -RemoveData -RemoveOEM -confirm:$false -ErrorAction SilentlyContinue
  Initialize-Disk -Number $Disk.Number -PartitionStyle MBR -ErrorAction SilentlyContinue
  $OSDrive = 'Z'
  $Partition = New-Partition -DiskNumber $Disk.Number -DriveLetter $OSDrive -UseMaximumSize -IsActive
  Format-Volume -Partition $Partition -FileSystem FAT32 -NewFileSystemLabel 'WinPE'
  Copy-Item "$WinADK\Media" -Path -Destination "$($OSDrive):" -Recurse
}
#endregion
